import React, { useState } from 'react';

import api from '../../services/api';

import { withStyles } from '@material-ui/core/styles'
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';

import 'bootstrap/dist/css/bootstrap.min.css';

import Logo from '../../img/LogoLGRolamentosLogin.png'

import './styles.css';

const ColorButton = styled(Button)<ButtonProps>(({ theme }) => ({
  color: theme.palette.getContrastText(purple[500]),
  backgroundColor: purple[500],
  '&:hover': {
    backgroundColor: purple[700],
  },
}));

function Logon() {
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');

  async function handleLogin(e) {
    e.preventDefault();

    try {
      const response = await api.post('api/login', { name, password });
      localStorage.setItem('token', response.data.token);
    } catch (err) {
      alert('Falha no login, tente novamente.');
    }
  }

  return (
      <div className='login'> 
        <Container component="main" maxWidth='xs'>
          <div className='mt-3 mt-md-5 d-flex flex-column align-items-center'>
            <img className='' src={Logo} alt='Logo'/>
          </div>

          <div className='form'>
            <TextField className='mb-4' 
              helperText=""
              id="standard-basic" 
              color='primary'
              label="Usuário" 
              variant="standard"
              fullWidth />

            <TextField className='mb-3'
              helperText=""
              id="standard-basic" 
              color='primary'
              label="Senha" 
              variant="standard"
              fullWidth />

              <a className='ms-1' href="">Esqueci minha senha</a>

            <button className="button" type="submit">Entrar</button>
            <ColorButton variant="contained">Custom CSS</ColorButton>
          </div>
        </Container>
      </div>
      );
}

export default Logon;
